import * as React from 'react';
import ReactDOM from 'react-dom';
import { IconButton, Box } from "@material-ui/core";
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import MoreHorizIcon from '@mui/icons-material/MoreHoriz';
import { MaskedTextBox } from 'smart-webcomponents-react/maskedtextbox';
import { Input } from 'smart-webcomponents-react/input';
import ArrowDropUpIcon from '@mui/icons-material/ArrowDropUp';
import { DateTimePicker } from 'smart-webcomponents-react/datetimepicker';
import { XMLrequest } from '../Url';
import { CheckBox } from 'smart-webcomponents-react/checkbox';
import { Calendar } from 'smart-webcomponents-react/calendar';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import Popover from '@mui/material/Popover';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import List from '@mui/material/List';
//import { ListBox, ListItem, ListItemsGroup } from 'smart-webcomponents-react/listbox';
export default function Editor(props) {

  const [blur, setBlur] = React.useState();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [anchorElList, setAnchorElList] = React.useState(null);
  const [DList, setList] = React.useState();

  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handlePopoverOpenList = (event) => {
    setAnchorElList(event.currentTarget);
  };
  const handlePopoverClose = () => {
    setAnchorEl(null);

  };
  const handlePopoverCloseList = () => {
    setAnchorElList(null);
  };

  const open = Boolean(anchorEl);
  const openList = Boolean(anchorElList);
  let DropList, list;
  const EditStyle_PickList = 1;
  const EditStyle_Calendar = 2;
  const EditStyle_Check = 4;
  const EditStyle_UpDown = 8;
  const EditStyle_Ellipsis = 16;
  const EditStyle_CheckList = 32;
  const EditStyle_ReadOnly = 64;
  const EditStyle_EditList = 128;
  const EditStyle_ColorBox = 256;
  const EditStyle_AutoList = 512;
  const EditStyle_Password = 1024;

  const mcs_Unchecked = 0;
  const mcs_Checked = 1;
  const mcs_LeftArrow = 2;
  const mcs_UpArrow = 3;
  const mcs_Horizontal = 4;
  const mcs_Vertical = 5;
  const mcs_RightArrow = 6;
  const mcs_DownArrow = 7;
  const mcs_Bullet = 8;
  const mcs_Required = 9;
  const mcs_LeftArrowBlue = 10;
  const mcs_RightArrowRed = 11;
  const mcs_Unknown = 12;
  const mcs_Reject = 13;

  const Unchecked = 'url("data:image/svg+xml;base64,")'
  const Checked = 'url("data:image/svg+xml;base64,")'
  const LeftArrow = 'url("data:image/svg+xml;base64,")'
  const UpArrow = 'url("data:image/svg+xml;base64,")'
  const Horizontal = 'url("data:image/svg+xml;base64,")'
  const Vertical = 'url("data:image/svg+xml;base64,")'
  const RightArrow = 'url("data:image/svg+xml;base64,")'
  const DownArrow = 'url("data:image/svg+xml;base64,")'
  const Bullet = 'url("data:image/svg+xml;base64,")'
  const Required = 'url("data:image/svg+xml;base64,")'
  const LeftArrowBlue = 'url("data:image/svg+xml;base64,")'
  const RightArrowRed = 'url("data:image/svg+xml;base64,")'
  const Unknown = 'url("data:image/svg+xml;base64,")'
  const Reject = 'url("data:image/svg+xml;base64,")';

  var p = 0;
  if (props.EditStyle & EditStyle_Calendar) p = p + 1
  else {
    if (props.EditStyle & EditStyle_PickList) p = p + 1
    if (props.EditStyle & EditStyle_UpDown) p = p + 1
    if (props.EditStyle & EditStyle_Ellipsis) p = p + 1
  }
  p = p * 24
  p = p + 'px'
  let lp
  if (props.EditStyle & EditStyle_Check)
    lp = "28px"
  else
    lp = "4px"
  var CheckID = "State_" + props.SectionID + "_" + props.id
  var EditID = props.SectionID + "_" + props.id  //CreateName()
  function CreateName(count) {
    var res, s = "0123456789ABCDEFGHIKLMNOPQRSTVXYZ";
    if (!count) count = 20
    for (var n = 0; n <= count; n++) {
      if (n === 0) {
        res = s[Math.floor(Math.random() * (count - 1) + 1)];
      } else {
        res = res + s[Math.floor(Math.random() * (count - 1) + 1)];
      }
    }
    return res
  }
  function SelectCheckState(e) {
    let btn = e.currentTarget;
    let CheckState = btn.dataset.checkstate;
    let parent = document.getElementById(btn.getAttribute("for"))
    let Check = document.getElementById(btn.dataset.checkid)
    let selectCheckState = SelectBackgroundCheck(CheckState)
    Check.style.backgroundImage = selectCheckState
    if (parent)
    {
      parent.setAttribute("data-checkState", CheckState)
    }
    EnterValue(parent)
    handlePopoverClose()
  }

  function ButtonGroupCheck() {
    let countCheckState = [];
    if (props.MultiCheckSet & Math.pow(2, mcs_Checked)) {
      countCheckState.push(<IconButton
        data-CheckState={props.CheckState}
        for={EditID}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Checked, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)} onMouseOut={(e) => MouseOutCheck(e)}
        onClick={(e) => SelectCheckState(e)}>
      </IconButton>)
    }

    if (props.MultiCheckSet & Math.pow(2, mcs_Bullet)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-CheckState={mcs_Bullet}
        data-checkid={CheckID}
        for={EditID}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Bullet, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_DownArrow)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-CheckState={mcs_DownArrow}
        data-checkid={CheckID}
        for={EditID}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: DownArrow, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_Horizontal)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_Horizontal}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Horizontal, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_LeftArrow)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_LeftArrow}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: LeftArrow, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_LeftArrowBlue)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_LeftArrowBlue}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: LeftArrowBlue, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_Reject)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_Reject}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Reject, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_Required)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_Required}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Bullet, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_RightArrow)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_RightArrow}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Required, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_RightArrowRed)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_RightArrowRed}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: RightArrowRed, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_Unknown)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-checkctate={mcs_Unknown}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Unknown, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_UpArrow)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={props.CheckState}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: UpArrow, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (Number(props.MultiCheckSet) & Math.pow(2, mcs_Vertical)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={props.CheckState}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Vertical, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    if (props.MultiCheckSet & Math.pow(2, mcs_Unchecked)) {
      countCheckState.push(<IconButton
        onClick={(e) => SelectCheckState(e)}
        data-checkid={CheckID}
        for={EditID}
        data-CheckState={mcs_Unchecked}
        style={{ padding: "0px", width: props.style.height, height: props.style.height, backgroundImage: Unchecked, backgroundRepeat: "no-repeat" }}
        onMouseOver={(e) => MouseOverCheck(e)}
        onMouseOut={(e) => MouseOutCheck(e)}></IconButton>)
    }
    let gb
    if (countCheckState.length > 2) {
      gb = <ButtonGroup orientation="vertical"
        aria-label="vertical contained button group"
        variant="text" style={{ width: props.style.height, height: Number(props.style.height.replace(/[^+\d]/g, '')) * countCheckState.length + 2 + "px" }}>
        {countCheckState.map((item) => { return item })}
      </ButtonGroup>
      return <Popover
        id="mouse-over-popover"
        sx={{
          pointerEvents: 'all',
          height: 200
        }}
        open={open}
        anchorEl={anchorEl}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
        onClose={handlePopoverClose}>
        {gb}
      </Popover>
    } else
      return <></>

  }
  function MouseOverCheck(e) {
    let div = e.currentTarget;
    div.style.transform = "scale(1.2)"
  }
  function MouseOutCheck(e) {
    let div = e.currentTarget;
    div.style.transform = ""
  }

  function ClickListMenu(e) {
    let div = e.currentTarget;
    let parent;
    parent = div;
    while (!parent.classList.contains("basic-single")) {
      parent = parent.parentNode;
    }
    let lbl = parent.querySelector("#react-select-3-placeholder")
    if (lbl) {
      lbl.innerText = div.innerText;
      let indexVal = div.getAttribute("indexval")
      if (props.onCloseUpList)
        props.onCloseUpList(indexVal)
    }
    let list = parent.querySelector("div.select__menu")
    if (list) {
      list.remove();
    }
  }
  function CreateList(items, parent) {
    function CloseList(ev) {
      let p
      if (ev) p = ev.currentTarget;
     // let list = document.getElementById("mouse-over-popover-list");
     // const withinBoundaries = ev.composedPath().includes(list);
      if (!p)
        p = document.getElementById("mouse-over-popover")
      if (p)
        p.remove()
    }
    function ClickItemList(ev) {
      let p = ev.currentTarget;
      parent.dataset.value = p.innerText;
      parent.dataset.objref = p.dataset.index
      EnterValue(parent)
      CloseList()
    }

    function FindItems(ev) {
      let input = ev.currentTarget;
      let text = input.value.toUpperCase()
      let list = input.parentNode.parentNode.nextSibling.childNodes
      let item, valItem
      for (let i = 0; i <= list.length - 1; i = i + 1) {
        item = list[i]
        valItem = item.dataset.value.toUpperCase();
        if (valItem.indexOf(text) == -1) {
          item.style.display = "none"
        }
        else
          item.style.display = ""
      }
    }
    let itemList
    let it = [];
    let width = parent.getBoundingClientRect().width
    let index = 0, indexv, val = parent.dataValue

    for (let pair of items) {
      let text

      text = pair[1]
      if (typeof text == "string") {
        if (val === text) {
          indexv = index
        }
        it.push(<ListItem ref={parent} key={index} component="div" disablePadding data-value={text}>
          <ListItemButton style={{ padding: 0, height: "24px", width: "inherit" }}>
            <ListItemText
              disableTypography
              primary={text}
              data-parent={parent.id}
              data-index={pair[0]}
              style={{ paddingLeft: "10px", fontFamily: "Roboto, Helvetica Neue, sans-serif", fontSize: "14px", whiteSpace: "nowrap", textOverflow: "ellipsis", overflow: "hidden" }}
              onClick={(ev) => ClickItemList(ev)}
            />
          </ListItemButton>
        </ListItem>)
      }
      index = index + 1;
    }
    let top
    if (it.length <= 1)
      top = 5
    else
      top = 30
    itemList = <Box
      style={{
        position: "absolute",
        backgroundColor: "white",
        border: "2px solid",
        width: width + "px",
        left: parent.getBoundingClientRect().left + "px",
        top: parent.getBoundingClientRect().top + parent.getBoundingClientRect().height + "px"
      }}
      id="mouse-over-popover-list">
      {it.length <= 1 ? <></> : <Box className="smart-list-box">
        <div className="smart-list-box-filter-input-container vscroll" role="presentation" smart-id="filterInputContainer" style={{
          position: "fixed", zIndex: 1, width: width - 8 + "px", marginLeft: "2px",
          paddingLeft: "0px", marginTop: "0px"
        }}>
          <input placeholder="Поиск ..." role="searchbox" aria-label="Поиск ..." smart-id="filterInput" onKeyUp={(ev) => FindItems(ev)} /></div></Box>}
      <List style={{ maxHeight: 200 - top + "px", width: width - 2 + "px", top: top + "px" }} >
        {it.map((item) => { return item })}
      </List></Box>
    let background = document.createElement("div")
    background.id = "mouse-over-popover"
    background.addEventListener("click", CloseList)
    background.style = "position: fixed; z-index: 1300; right: 0;bottom: 0;top: 0;left: 0;pointer-events: all;"
    ReactDOM.render(itemList, background)
    document.body.appendChild(background)
    //return itemList
  }
  function onDropDownList(e) {
    let div = e.currentTarget;
    let id = div.getAttribute("for");
    let edit = document.getElementById(id)
    if (props.onDropDownList) {
      list = props.onDropDownList(edit)
      if (list) {
        CreateList(list, edit)
        // setAnchorElList(edit)
      }
    }
  }

  if (props.list) {
    list = props.list.split(',')
  }
  function EnterValue(ev, TextChanged) {
    props.onEdit(ev, TextChanged)
  }
  function EditFocus(e) {
    let el = e.currentTarget;
    setBlur(el)
    let val = el.dataEditval;
    if (val) {
      el.value = val
      el.select(true)
    }
  }
  function SetValueEdit(e) {
    let el = blur;
    let val = el.dataValue;
    // EnterValue(el, 1)
    if (val) el.value = val
  }


  function SelectBackgroundCheck(CheckState) {
    let res
    if (CheckState) {
      switch (Number(CheckState)) {
        case mcs_Checked: res = Checked; break;
        case mcs_Unchecked: res = Unchecked; break;
        case mcs_Bullet: res = Bullet; break;
        case mcs_DownArrow: res = DownArrow; break;
        case mcs_Horizontal: res = Horizontal; break;
        case mcs_LeftArrow: res = LeftArrow; break;
        case mcs_LeftArrowBlue: res = LeftArrowBlue; break;
        case mcs_Reject: res = Reject; break;
        case mcs_Required: res = Required; break;
        case mcs_RightArrow: res = RightArrow; break;
        case mcs_RightArrowRed: res = RightArrowRed; break;
        case mcs_Unknown: res = Unknown; break;
        case mcs_UpArrow: res = UpArrow; break;
        case mcs_Vertical: res = Vertical; break;
        default: res = Unchecked; break;
      }
    }
    else
      switch (Number(props.CheckState)) {
        case mcs_Checked: res = Checked; break;
        case mcs_Unchecked: res = Unchecked; break;
        case mcs_Bullet: res = Bullet; break;
        case mcs_DownArrow: res = DownArrow; break;
        case mcs_Horizontal: res = Horizontal; break;
        case mcs_LeftArrow: res = LeftArrow; break;
        case mcs_LeftArrowBlue: res = LeftArrowBlue; break;
        case mcs_Reject: res = Reject; break;
        case mcs_Required: res = Required; break;
        case mcs_RightArrow: res = RightArrow; break;
        case mcs_RightArrowRed: res = RightArrowRed; break;
        case mcs_Unknown: res = Unknown; break;
        case mcs_UpArrow: res = UpArrow; break;
        case mcs_Vertical: res = Vertical; break;
        default: res = Unchecked; break;
      }
    return res
  }
  DropList = props.EditStyle & EditStyle_Calendar ? <Box data-id={props.id} style={{ position: "relative", width: "100%", ...props.style }}>
    <Input style={{ width: "100%", height: "100%", borderRadius: "0px", borderRightWidth: "0px", borderTopWidth: "0px", borderLeftWidth: "0px", borderColor: "black" }} type="date" value={props.value}
      onChange={(ev) => props.onEdit(ev)}

      id={EditID}
      data-id={props.id}
      data-path={props.Path}
      data-objref={props.ObjRef}
      data-editval={props.EditVal}
      data-sectionid={props.SectionID}
      data-value={props.value ? props.value : ""} />
  </Box> :
    <Box className='basic-single css-b62m3t-container' data-id={props.id} data-sectionid={props.sectionid} style={{ position: "relative", width: "100%", ...props.style }} >
      <Box>
        {props.mask ?
          <MaskedTextBox id={EditID} data-id={props.id} onChange={(ev) => props.onEdit(ev)} style={{ height: props.style.height, padding: "0px", width: "100%", paddingRight: p, paddingLeft: lp, borderRadius: "0px", borderRightWidth: "0px", borderTopWidth: "0px", borderLeftWidth: "0px", borderColor: "black" }} value={props.caption ? props.caption : props.value ? props.value : ""} mask={props.mask} /> :
          <Input data-path={props.Path}
            id={EditID}
            data-id={props.id}
            data-objref={props.ObjRef}
            data-editval={props.EditVal}
            data-sectionid={props.SectionID}
            data-checkstate={props.CheckState}
            //  onKeyDown={(ev) => EnterValue(ev)}
            data-type={props.Type}
            onClick={(e) => EditFocus(e)}
            onChange={(ev) => props.onEdit(ev, 1)}
            onBlur={(e) => SetValueEdit(e)}
            value={props.caption ? props.caption : props.value ? props.value : ""}
            data-value={props.caption ? props.caption : props.value ? props.value : ""}
            style={{ height: props.style.height, padding: "0px", width: "100%", paddingRight: p, paddingLeft: lp, borderRadius: "0px", borderRightWidth: "0px", borderTopWidth: "0px", borderLeftWidth: "0px", borderColor: "black" }} >
          </Input>
        }
      </Box>

      {props.EditStyle ?
        <Box style={{ position: "absolute", top: "0px", right: "0px" }}>
          <ButtonGroup variant="text" aria-label="text button group">
            {props.EditStyle & EditStyle_Ellipsis ?
              <IconButton style={{ padding: "0px", minWidth: props.style.height }} ><MoreHorizIcon style={{ position: "relative", top: "3px", fill: "black" }} /></IconButton>
              :
              <></>}
            {props.EditStyle & EditStyle_PickList ?
              <>
                <IconButton
                  onClick={onDropDownList}
                  for={EditID}
                  style={{ padding: "0px", minWidth: props.style.height }}
                  aria-owns={openList ? 'mouse-over-popover' : undefined} aria-haspopup="true">
                  <ArrowDropDownIcon style={{ fill: "black" }} />

                </IconButton>

              </>
              :
              <></>}
            {props.EditStyle & EditStyle_UpDown ?
              <ButtonGroup orientation="vertical"
                aria-label="vertical contained button group"
                variant="text">
                <IconButton style={{ padding: "0px", minWidth: props.style.height, borderRightWidth: "0px", height: (Number(props.style.height.replace(/[^+\d]/g, '')) / 2) + "px" }} >
                  <ArrowDropUpIcon style={{ position: "relative", height: "100%", fill: "black" }} viewBox="6 5 12 12" />
                </IconButton>
                <IconButton style={{ padding: "0px", minWidth: props.style.height, height: (Number(props.style.height.replace(/[^+\d]/g, '')) / 2) + "px" }} >
                  <ArrowDropDownIcon style={{ position: "relative", height: "100%", fill: "black", top: "-4px" }} viewBox="6 5 12 12" />
                </IconButton>
              </ButtonGroup >
              :
              <></>}
          </ButtonGroup>
        </Box>
        : <></>
      }


      {props.EditStyle & EditStyle_Check ? <Box style={{ position: "absolute", top: "0px", left: "0px", minWidth: props.style.height, height: props.style.height }} >
        <IconButton id={CheckID} style={{ padding: "0px", width: "100%", height: "100%", backgroundImage: SelectBackgroundCheck() }} aria-owns={open ? 'mouse-over-popover' : undefined}
          aria-haspopup="true"
          onClick={handlePopoverOpen}
        ></IconButton>
        {
          ButtonGroupCheck()
        }
      </Box> : <></>}
    </Box>
  return DropList
}