import { Input, TextField, Typography } from "@material-ui/core";
import { Box, IconButton } from "@mui/material";
import { Splitter, SplitterItem, SplitterBar } from 'smart-webcomponents-react/splitter';
import Editor from "../../../Editor/Editor";
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
import Split from 'react-split'
import React from "react";
import ReactDOM from 'react-dom';
import { XMLrequest } from '../../../Url';
export default function Params(props) {
    const [data, SetData] = React.useState(props.data.Items)
    React.useEffect(() => {
        SetData(props.data.Items)
        }, [props.data.Items]);
    //var data = props.data.Items;
    const ParamSetOption_Disabled = 1;
    const ParamSetOption_InConfig = 2;
    const ParamSetOption_Calculated = 4;
    const ParamSetOption_Detalization = 8;
    const ParamSetOption_Markable = 16;
    const ParamSetOption_Collapsed = 32;
    const ParamSetOption_Reference = 64;
    const ParamSetOption_RedNegative = 128;
    const ParamSetOption_Inspector = 256;
    const ParamSetOption_Grid = 512;
    const ParamSetOption_Sortable = 1024;
    const ParamSetOption_Filterable = 2048;
    const ParamSetOption_Override = 4096;
    const ParamSetOption_Hidden = 8192;
    const ParamSetOption_History = 16384;
    const ParamSetOption_CanGroup = 32768;
    const ParamSetOption_Fixed = 65536;
    const ParamSetOption_NotEdit = 131072;

    const mcs_Unchecked = 0;
    const mcs_Checked = 1;
    const mcs_LeftArrow = 2;
    const mcs_UpArrow = 3;
    const mcs_Horizontal = 4;
    const mcs_Vertical = 5;
    const mcs_RightArrow = 6;
    const mcs_DownArrow = 7;
    const mcs_Bullet = 8;
    const mcs_Required = 9;
    const mcs_LeftArrowBlue = 10;
    const mcs_RightArrowRed = 11;
    const mcs_Unknown = 12;
    const mcs_Reject = 13;

    const Unchecked = 'data:image/svg+xml;base64,'
    const Checked = 'data:image/svg+xml;base64,'
    const LeftArrow = 'data:image/svg+xml;base64,'
    const UpArrow = 'data:image/svg+xml;base64,'
    const Horizontal = 'data:image/svg+xml;base64,'
    const Vertical = 'data:image/svg+xml;base64,'
    const RightArrow = 'data:image/svg+xml;base64,'
    const DownArrow = 'data:image/svg+xml;base64,'
    const Bullet = 'data:image/svg+xml;base64,'
    const Required = 'data:image/svg+xml;base64,")'
    const LeftArrowBlue = 'data:image/svg+xml;base64,'
    const RightArrowRed = 'data:image/svg+xml;base64,'
    const Unknown = 'data:image/svg+xml;base64,'
    const Reject = 'data:image/svg+xml;base64,';

    function SelectBackgroundCheck(CheckState) {
        let res
        if (CheckState) {
            switch (Number(CheckState)) {
                case mcs_Checked: res = Checked; break;
                case mcs_Unchecked: res = Unchecked; break;
                case mcs_Bullet: res = Bullet; break;
                case mcs_DownArrow: res = DownArrow; break;
                case mcs_Horizontal: res = Horizontal; break;
                case mcs_LeftArrow: res = LeftArrow; break;
                case mcs_LeftArrowBlue: res = LeftArrowBlue; break;
                case mcs_Reject: res = Reject; break;
                case mcs_Required: res = Required; break;
                case mcs_RightArrow: res = RightArrow; break;
                case mcs_RightArrowRed: res = RightArrowRed; break;
                case mcs_Unknown: res = Unknown; break;
                case mcs_UpArrow: res = UpArrow; break;
                case mcs_Vertical: res = Vertical; break;
                default: res = Unchecked; break;
            }
        }
        else
            res = Unchecked;
        return res
    }
    function MouseOverCheck(e) {
        let div = e.currentTarget;
        div.style.transform = "scale(1.2)"
    }
    function MouseOutCheck(e) {
        let div = e.currentTarget;
        div.style.transform = ""
    }

    function ClickCheckState(ev) {
        let check = ev.currentTarget
        let params = new Map();
        params.set('prefix', 'programs');
        params.set('comand', 'SetParamProperty');
        params.set('ID', check.dataset.id);
        params.set('Path', check.dataset.path);
        params.set('WSM', "1");
        let mapCheck = []
        if (check.dataset.multicheckset & Math.pow(2, mcs_Checked))
            mapCheck.push(mcs_Checked)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Unchecked))
            mapCheck.push(mcs_Unchecked)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Bullet))
            mapCheck.push(mcs_Bullet)
        if (check.dataset.multicheckset & Math.pow(2, mcs_DownArrow))
            mapCheck.push(mcs_DownArrow)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Horizontal))
            mapCheck.push(mcs_Horizontal)
        if (check.dataset.multicheckset & Math.pow(2, mcs_LeftArrow))
            mapCheck.push(mcs_LeftArrow)
        if (check.dataset.multicheckset & Math.pow(2, mcs_LeftArrowBlue))
            mapCheck.push(mcs_LeftArrowBlue)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Reject))
            mapCheck.push(mcs_Reject)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Required))
            mapCheck.push(mcs_Required)
        if (check.dataset.multicheckset & Math.pow(2, mcs_RightArrow))
            mapCheck.push(mcs_RightArrow)
        if (check.dataset.multicheckset & Math.pow(2, mcs_RightArrowRed))
            mapCheck.push(mcs_RightArrowRed)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Unknown))
            mapCheck.push(mcs_Unknown)
        if (check.dataset.multicheckset & Math.pow(2, mcs_UpArrow))
            mapCheck.push(mcs_UpArrow)
        if (check.dataset.multicheckset & Math.pow(2, mcs_Vertical))
            mapCheck.push(mcs_Vertical)
        let indexCheck = mapCheck.indexOf(Number(check.dataset.checkstate), 0)
        if (indexCheck >= 0) {
            if (indexCheck == mapCheck.length - 1)
                indexCheck = -1
            check.src = SelectBackgroundCheck(mapCheck[indexCheck + 1])
            check.dataset.checkstate = mapCheck[indexCheck + 1]
            params.set('checkstate', check.dataset.checkstate);
        }
        let otv = XMLrequest(params);
        if (otv.Values[0].CheckState)
            check.dataset.checkstate = otv.Values[0].CheckState
        check.dataset.id = otv.Values[0].ID
    }
    function ShowHideParams(e) {
        let el, params = new Map();
        if (e.currentTarget) {
            el = e.currentTarget

        }
        else
            el = e

        let tr = el.parentNode.parentNode;
        let lvl = Number(tr.dataset.level)
        let NextTR = tr.nextElementSibling;
        let NextLvl = Number(NextTR.dataset.level);
        params.set('prefix', 'programs');
        params.set('comand', 'ParamCollapsed');
        params.set('ID', tr.dataset.id);
        params.set('Path', props.data.Path);
        if (tr.dataset.collapsed === "1") {
            params.set('Collapsed', "0");
            tr.dataset.collapsed = 0;
            el.style.transform = "rotate(0deg)"
            while (NextLvl !== lvl) {
                if (lvl == NextLvl - 1) {
                    NextTR.style.display = ""
                }
                NextTR = NextTR.nextElementSibling;
                NextLvl = Number(NextTR.dataset.level);
            }
        }
        else {
            tr.dataset.collapsed = 1;
            params.set('Collapsed', "1");
            el.style.transform = "rotate(-90deg)"
            while (NextLvl !== lvl) {
                // if (lvl == NextLvl - 1)
                NextTR.style.display = "none"
                NextTR = NextTR.nextElementSibling;
                NextLvl = Number(NextTR.dataset.level);

            }
        }
        if (e.currentTarget)
            XMLrequest(params)
    }
    function onDropDownList(el) {
        let res = new Map();
        let params = new Map();
        params.set('prefix', 'programs');
        params.set('comand', 'GetParamValues');
        params.set('ID', el.dataset.id);
        params.set('Path', props.data.Path);
        let otv = XMLrequest(params).Items
        for (let i = 0; i <= otv.length - 1; i = i + 1) {
            let item = otv[i];
            if (item.id) {
                res.set(otv[i].id, otv[i].text)
            }
            else
                res.set(i, otv[i])
        }
        /*  for (var key in otv) {
              //if (otv.hasOwnProperty(key)) {
                if (otv[key].id)
                res.set(otv[key].id, otv[key].text)
                else
                res.set(0, otv[key])
              
            }*/
        return res
    }

    function onEdit(ev, TextChanged) {
        let el
        if (ev.type == "change") {
            el = ev.currentTarget
        }
        else {
            el = ev;
            TextChanged = 0
        }
        if (el) {
            let val
            val = el.value;
            if (el.type == 'date') {
                val = val.split("-");
                val = val[2] + "." + val[1] + "." + val[0]
            }
            let params = new Map();
            params.set('prefix', 'programs');
            params.set('comand', 'SetParamProperty');
            params.set('ID', el.dataset.id);
            params.set('Path', props.data.Path);
            //  if (TextChanged)
            params.set('TextChanged', "1")
            //  else
            //     params.set('TextChanged', "0")
            params.set('WSM', "1");
            if (val !== "")
                params.set('Value', val);
            if (el.dataset.checkstate)
                params.set('CheckState', el.dataset.checkstate)
            if (el.dataset.objref)
                params.set('ObjRef', el.dataset.objref)
            else
                params.set('ObjRef', 0)
            let otv = XMLrequest(params);
            if (otv) {
                //  el.value = otv.Values[0].Value;
                // el.setAttribute("data-objref", otv.Values[0].ObjRef)
                //  el.setAttribute("data-id", otv.Values[0].ID)
                if (otv.NeedRefresh) {
                    let params = new Map();
                    params.set('prefix', 'reports');
                    params.set('comand', 'GetReportParams');
                    params.set('ReportID', props.id);
                    params.set('SectionID', props.SectionID);
                    params.set('NeedRefresh', 1);
                    let data = XMLrequest(params);
                    SetData(data.Items)
                }
                else return otv.Values[0]

            }
            else
                return val

        }
    }
    var tMouse = {
        // isMouseDown
        // tMouse.target
        // tMouse.targetWidth
        // targetPosX
    };
    //  window.addEventListener("mousemove", MouseMove);
    // window.addEventListener("mouseup", MouseUp);
    function MouseDown(e) {

        tMouse.isMouseDown = true;
        let element = e.target.parentElement.parentElement.parentElement.parentElement;
        tMouse.parent = element
        element = element.querySelector("td")
        tMouse.target = element;
        tMouse.targetWidth = element.clientWidth;
        tMouse.targetPosX = element.getBoundingClientRect().x;
    }
    function MouseMove(e) {
        if (tMouse.target || tMouse.isMouseDown) {
            let size = (e.clientX - tMouse.targetWidth) - tMouse.targetPosX;
            let width = tMouse.targetWidth + size
            let widthTable = tMouse.parent.getBoundingClientRect().width
            let proc = (width / widthTable) * 100
            let proc2 = 100 - proc
            if ((proc < 30))
                proc = 30
            else if (proc2 < 30)
                proc = 70
            tMouse.target.style.width = proc + "%";
            tMouse.target.nextElementSibling.style.width = 100 - proc + "%"
        }
    }
    function MouseUp(e) {
        tMouse = {}
    }

    function CreateEdit(event, item) {
        if (event.target.tagName == "TD") {
            HiddenEdit(event.target.parentNode.parentNode.parentNode, event.target)
            let container = document.createElement("div")
            let val = item.EditVal ? item.EditVal : item.value
            if (item.EditStyle == 2) {
                val = val.split(".")
                if (val.length) {
                    val = val[2] + "-" + val[1] + "-" + val[0]
                }
                else
                    val = item.EditVal ? item.EditVal : item.Value
            }
            let edit = <Editor value={val}
                EditStyle={item.EditStyle}
                style={{ height: "24px" }}
                mask={item.EditMask}
                id={item.ID}
                CLSID={item.CLSID}
                Path={props.data.Path}
                ObjRef={item.ObjRef}
                EditVal={item.Value}
                SectionID={props.SectionID}
                //  setdata={SetData}
                onDropDownList={onDropDownList}
                // onEdit={onEdit}
                ReportID={props.id}
                CheckState={item.CheckState}
                MultiCheckSet={item.MultiCheckSet}
                Type="ParamItem"
                parent={event.target}
            />
            ReactDOM.render(edit, container)

        }
    }
    function HiddenEdit(parent, td) {
        let edit = parent.querySelectorAll(".text-field__icon")

        for (let n = 0; n <= edit.length - 1; n++) {
            let el = edit[n];
            let inp = el.querySelector("input")
            let val = inp.value
            let oldVal = inp.dataset.value
            let otv
            if (val !== oldVal)
                otv = onEdit(inp)//.dispatchEvent(new Event('change'))
            else
                val = inp.dataset.editval
            if (otv)
                if (otv.Value) {
                    val = otv.Value
                }
            if (!val)
                val = ""
                let td =  el.parentNode.parentNode
                td.innerText = val
            let check = el.querySelector(".Check");
            if (check)
               td.appendChild(check)
            el.remove()
        }

    }
   //  data=props.data.Items
    let param = <table onMouseMove={MouseMove}
    onMouseUp={MouseUp}
    id={"params_" + props.SectionID + "_" + props.id}
    style={{ borderSpacing: '0px', tableLayout: "fixed", width: "100%" }}>
    <tbody>
        {data.map((item, i) => {
            var display = (item.Options & ParamSetOption_Disabled) || (item.Options & ParamSetOption_Hidden) ? "none" : ""
            let level = Number(item.Level);
            let levelNext
            if (data[i + 1])
                levelNext = Number(data[i + 1].Level);
            if (!level) {
                level = 0;
            }
            let padding = level * 24;
            return (
                <tr style={{ display: display }}
                    data-level={level ? level : '0'}
                    data-collapsed={item.Options & ParamSetOption_Collapsed ? 1 : 0}
                    data-id={item.ID}>
                    <td data-level={level ? level : '0'}
                        data-collapsed={item.Options & ParamSetOption_Collapsed ? 1 : 0}
                        data-id={item.ID}
                        style={{ borderBottom: "1px solid", position: "relative", whiteSpace: "nowrap", paddingLeft: padding ? padding : 12 }}>
                        <span
                            style={{
                                display: "block",
                                position: "relative",
                                textOverflow: "clip",
                                overflow: "hidden"
                            }}>
                            {item.Name}
                        </span>
                        {level < levelNext ?
                            <img className = "Collapsed" onClick={(e) => ShowHideParams(e)} style={{ transform: item.Options & ParamSetOption_Collapsed ? "rotate(-90deg)" : "", height: "10px", width: "10px", position: "absolute", left: level * 12, top: "calc(50% - 5px)" }} src="data:image/png;base64,"></img>
                            : <></>}
                        <div style={{ width: "3px", height: "100%", position: "absolute", right: "0px", backgroundColor: "#d3d3d3", top: "0px", cursor: "col-resize" }}
                            onMouseDown={MouseDown}
                        >
                        </div>
                    </td>
                    <td
                        style={{ borderBottom: "1px solid", position: "relative", whiteSpace: "nowrap", display: item.MultiCheckSet ? "grid": null, paddingLeft: item.MultiCheckSet ? 2 : 24 }}
                        onClick={(e) => CreateEdit(e, item)}
                    >
                        {item.MultiCheckSet ?
                            <img
                                onMouseOver={(e) => MouseOverCheck(e)}
                                onMouseOut={(e) => MouseOutCheck(e)}
                                data-Path={props.data.Path}
                                data-CheckState={item.CheckState ? item.CheckState : 0}
                                data-MultiCheckSet={item.MultiCheckSet}
                                data-Id={item.ID}
                                style={{ width: 20, height: 20 }} src={SelectBackgroundCheck(item.CheckState ? item.CheckState : 0)} onClick={ClickCheckState} /> : ""}
                        {item.Value ? item.Value : ""}
                    </td>
                </tr>
            )


        })
        }
    </tbody>
</table>
if (props.parent)
{
    let container = document.createElement("div")
    ReactDOM.render(param, container)
    container = container.children[0]
    props.parent.appendChild(container)
}
    return param 
    
}